","keccak256":"0xa1e8e83cd0087785df04ac79fb395d9f3684caeaf973d9e2c71caef723a3a5d6","license":"MIT"},"contracts/DeedNFT.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract DeedNFT is ERC721, AccessControl {
    struct DeedInfo {
        bytes ipfsDetailsHash;
        AssetType assetType;
        uint256 price;
        string deedAddress;
        bool isValidated;
    }
    uint256 private _nextDeedId;
    mapping(uint256 => DeedInfo) private deedInfoMap;
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");

    enum AssetType {
        Land,
        Vehicle,
        Estate,
        CommercialEquipment
    }

    event DeedNFTMinted(uint256 deedId, DeedInfo deedInfo, address minter);
    event DeedNFTBurned(uint256 deedId, address burner);
    event DeedNFTAssetValidationSet(uint256 deedId, bool isValid);
    event DeedNFTIpfsDetailsSet(uint256 deedId, bytes newIpfsDetailsHash);
    event DeedNFTPriceUpdated(uint256 deedId, uint256 newPrice);
    event DeedNFTAssetTypeSet(uint256 deedId, AssetType newAssetType);

    constructor() ERC721("DeedNFT", "DEED") {
        _nextDeedId = 1;
        _setupRole(VALIDATOR_ROLE, _msgSender());
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    modifier deedExists(uint256 _deedId) {
        require(_exists(_deedId), string.concat("[DeedNFT] Deed does not exist with id ", Strings.toString(_deedId)));
        _;
    }

    modifier onlyOwner(uint256 _deedId) {
        require(
            _msgSender() == ownerOf(_deedId),
            string.concat("[DeedNFT] Must be owner of the Deed with id ", Strings.toString(_deedId))
        );
        _;
    }

    function mintAsset(
        address _owner,
        bytes memory _ipfsDetailsHash,
        AssetType _assetType,
        string memory _deedAddress
    ) public {
        _mint(_owner, _nextDeedId);
        DeedInfo storage deedInfo = deedInfoMap[_nextDeedId];
        deedInfo.ipfsDetailsHash = _ipfsDetailsHash;
        deedInfo.assetType = _assetType;
        deedInfo.deedAddress = _deedAddress;
        deedInfo.isValidated = false;
        emit DeedNFTMinted(_nextDeedId, deedInfo, _msgSender());
        _nextDeedId = _nextDeedId + 1;
    }

    function burn(uint256 _deedId) public onlyOwner(_deedId) {
        _burn(_deedId);
        emit DeedNFTBurned(_deedId, _msgSender());
    }

    function setAssetValidation(uint256 _deedId, bool _isValid) public onlyRole(VALIDATOR_ROLE) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.isValidated = _isValid;
        emit DeedNFTAssetValidationSet(_deedId, _isValid);
    }

    function setPrice(uint256 _deedId, uint32 _newPrice) public deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.price = _newPrice;
        emit DeedNFTPriceUpdated(_deedId, _newPrice);
    }

    function setIpfsDetailsHash(
        uint256 _deedId,
        bytes memory _ipfsDetailsHash
    ) public virtual deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.ipfsDetailsHash = _ipfsDetailsHash;
        emit DeedNFTIpfsDetailsSet(_deedId, _ipfsDetailsHash);
    }

    function setAssetType(
        uint256 _deedId,
        AssetType _assetType
    ) internal virtual deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.assetType = _assetType;
        emit DeedNFTAssetTypeSet(_deedId, _assetType);
    }

    function getDeedInfo(uint256 _deedId) public view deedExists(_deedId) returns (DeedInfo memory) {
        return deedInfoMap[_deedId];
    }

    function addValidator(address _validator) public onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(VALIDATOR_ROLE, _validator);
    }

    function removeValidator(address _validator) public onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(VALIDATOR_ROLE, _validator);
    }

    function canSubdivide(uint256 _deedId) external view returns (bool) {
        AssetType assetType = getDeedInfo(_deedId).assetType;
        return assetType == AssetType.Land || assetType == AssetType.Estate;
    }

    function supportsInterface(bytes4 _interfaceId) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }
}
","keccak256":"0x258d6ed01bcfbb921d9a68e28da359ad34e3374168ea195f1a394bd3280998d8","license":"AGPL-3.0"},"contracts/FundsManager.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "hardhat/console.sol";

contract FundsManager is Context {
    event FundsStored(uint256 id, IERC20 token, uint256 amount, address sender, address caller, uint256 newBalance);
    event FundsWithdrawn(
        uint256 id,
        IERC20 token,
        uint256 amount,
        address destination,
        address caller,
        uint256 newBalance
    );

    // AccountOwner     ->    AccountId   ->  TokenAddress -> Amount
    mapping(address => mapping(uint256 => mapping(address => uint256))) public accountsMapping;

    /**
     * Get the balance of an account
     * @param _id AccountId
     * @param _token TokenAddress
     * @return Balance
     */
    function balanceOf(uint256 _id, IERC20 _token) external view returns (uint256) {
        return accountsMapping[_msgSender()][_id][address(_token)];
    }

    /**
     * Pull tokens from sender and store them in the contract
     * @param _id AccountId
     * @param _token TokenAddress
     * @param _amount Amount
     * @param _sender Witch address to pull tokens from
     */
    function store(uint256 _id, IERC20 _token, uint256 _amount, address _sender) external {
        require(
            _token.allowance(_sender, address(this)) >= _amount,
            string.concat(
                "[FundsManager] Not enough allowance for account ",
                Strings.toString(_id),
                " and amount ",
                Strings.toString(_amount)
            )
        );

        _token.transferFrom(_sender, address(this), _amount);
        accountsMapping[_msgSender()][_id][address(_token)] += _amount;

        emit FundsStored(
            _id,
            _token,
            _amount,
            _sender,
            _msgSender(),
            accountsMapping[_msgSender()][_id][address(_token)]
        );
    }

    /**
     * Withdraw tokens from the contract and send them to the recipient
     * @param _id AccountId
     * @param _token TokenAddress
     * @param _amount Amount
     * @param _recipient Witch address to send tokens to
     */
    function widthdraw(uint256 _id, IERC20 _token, uint256 _amount, address _recipient) external {
        require(
            accountsMapping[_msgSender()][_id][address(_token)] >= _amount,
            string.concat(
                "[FundsManager] Not enough funds for account ",
                Strings.toString(_id),
                " and amount ",
                Strings.toString(_amount)
            )
        );

        accountsMapping[_msgSender()][_id][address(_token)] -= _amount;
        _token.transfer(_recipient, _amount);

        emit FundsWithdrawn(
            _id,
            _token,
            _amount,
            _recipient,
            _msgSender(),
            accountsMapping[_msgSender()][_id][address(_token)]
        );
    }
}
","keccak256":"0x0890e215a96e64f608cfbfd53456268b8e777f3c47b6200704667b7535a77974","license":"AGPL-3.0"},"contracts/LeaseAgreement.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./DeedNFT.sol"; // Import the IDeedNFT interface
import "./SubdivisionNFT.sol"; // Import the ISubdivisionNFT interface
import "./FundsManager.sol";

interface ILeaseNFT {
    function mint(address leaseOwner, uint256 leaseId) external;

    function burn(uint256 leaseId) external;
}

contract LeaseAgreement is Context, ReentrancyGuard {
    // CONSTANTS
    uint256 private constant MONTH = 30.5 days;

    struct LeaseDates {
        uint256 startDate;
        uint256 endDate;
        uint256 rentDueDate;
        uint256 distributableDate;
    }

    struct Deposit {
        uint256 amount;
        bool paid;
    }

    struct Lease {
        address lessor;
        address lessee;
        uint256 rentAmount;
        Deposit securityDeposit;
        uint256 latePaymentFee;
        uint32 gracePeriod;
        LeaseDates dates;
        uint256 extensionCount;
        uint256 propertyTokenId;
        address agent;
        uint8 agentPercentage;
        uint256 unclaimedRentAmount;
    }

    struct RentPaymentInfo {
        uint256 rentAmount;
        uint256 totalBalance;
        uint32 unpaidMonths;
        uint256 rentDueDate;
    }

    mapping(uint256 => Lease) public leases;
    uint256 public leaseCounter;
    ILeaseNFT public leaseNFT;
    IERC20 public paymentToken;
    DeedNFT public deedNFT;
    SubdivisionNFT public subdivisionNFT;
    FundsManager public fundsManager;

    event LeaseFundsManagerSet(address fundsManager);
    event LeasePaymentTokenSet(address paymentToken);
    event LeaseCreated(uint256 leaseId, Lease lease);
    event LeaseTerminated(uint256 leaseId);
    event LeasePaymentMade(uint256 leaseId, uint256 amount, uint256 unclaimedRentAmount);
    event LeaseRentDistributed(
        uint256 leaseId,
        uint256 lessorAmount,
        uint256 agentAmount,
        uint256 distributableDate,
        uint256 timestamp
    );
    event LeaseAgentAdded(uint256 leaseId, address agent, uint256 percentage);
    event LeaseAgentRemoved(uint256 leaseId);
    event LeaseDueDateChanged(uint256 leaseId, uint256 newDueDate);
    event LeaseDepositSubmited(uint256 leaseId, uint256 amount);
    event LeaseExtended(uint256 leaseId, uint256 endDate, uint256 rentAmount, uint256 extensionCount);

    constructor(
        address _leaseNFT,
        address _paymentToken,
        address _deedNFT,
        address _subdivisionNFT,
        address _fundsManager
    ) {
        require(_leaseNFT != address(0), "[Lease Agreement] Invalid LeaseNFT address");
        require(_paymentToken != address(0), "[Lease Agreement] Invalid xDai token address");
        require(_deedNFT != address(0), "[Lease Agreement] Invalid DeedNFT address");
        require(_subdivisionNFT != address(0), "[Lease Agreement] Invalid SubdivisionNFT address");

        leaseNFT = ILeaseNFT(_leaseNFT);
        deedNFT = DeedNFT(_deedNFT);
        subdivisionNFT = SubdivisionNFT(_subdivisionNFT);

        setPaymentToken(_paymentToken);
        setFundsManager(_fundsManager);

        leaseCounter = 0;
    }

    function setFundsManager(address _fundsManager) public {
        fundsManager = FundsManager(_fundsManager);
        emit LeaseFundsManagerSet(_fundsManager);
    }

    function setPaymentToken(address _paymentToken) public {
        paymentToken = IERC20(_paymentToken);
        emit LeasePaymentTokenSet(_paymentToken);
    }

    function addAgent(uint256 _leaseId, address _agent, uint8 _percentage) external {
        Lease storage lease = leases[_leaseId];
        require(_msgSender() == lease.lessor, "[Lease Agreement] Only the Lessor can set the Agent");
        require(_percentage >= 0 && _percentage <= 100, "[Lease Agreement] Invalid agent percentage");

        lease.agent = _agent;
        lease.agentPercentage = _percentage;

        emit LeaseAgentAdded(_leaseId, _agent, _percentage);
    }

    function removeAgent(uint256 _leaseId) external {
        Lease storage lease = leases[_leaseId];
        require(
            _msgSender() == lease.lessor || _msgSender() == lease.agent,
            "[Lease Agreement] only the Lessor or the Agent can remove the Agent"
        );

        lease.agent = address(0);
        lease.agentPercentage = 0;

        emit LeaseAgentRemoved(_leaseId);
    }

    function createLease(
        address _lessee,
        uint256 _startDate,
        uint256 _endDate,
        uint256 _rentAmount,
        uint256 _securityDepositAmount,
        uint256 _propertyTokenId,
        uint256 _latePaymentFee,
        uint32 _gracePeriod
    ) external {
        require(_lessee != address(0), "[Lease Agreement] Invalid lessee address");
        require(_startDate < _endDate, "[Lease Agreement] Invalid start and end dates");
        require(
            _endDate - _startDate > 1 * MONTH,
            "[Lease Agreement] End date and start date should be 1 month appart"
        );
        require(_gracePeriod >= 3);
        require(_latePaymentFee >= 0 && _latePaymentFee <= 15);
        bool isDeedOwner = _verifyDeedOwnership(_msgSender(), _propertyTokenId);
        bool isSubdivisionOwner = _verifySubdivisionOwnership(_msgSender(), _propertyTokenId);
        require(isDeedOwner || isSubdivisionOwner, "[Lease Agreement] Lessor must own the property NFT");

        uint256 leaseId = leaseCounter;
        leaseCounter++;
        Lease storage lease = leases[leaseId];
        lease.lessor = _msgSender();
        lease.lessee = _lessee;
        lease.dates.startDate = _startDate;
        lease.dates.endDate = _endDate;
        lease.rentAmount = _rentAmount;
        lease.securityDeposit.amount = _securityDepositAmount;
        lease.dates.rentDueDate = _startDate + 1 * MONTH;
        lease.extensionCount = 0;
        lease.propertyTokenId = _propertyTokenId;
        lease.securityDeposit.paid = _securityDepositAmount == 0;
        lease.latePaymentFee = _latePaymentFee;
        lease.gracePeriod = _gracePeriod;
        leaseNFT.mint(_msgSender(), leaseId);
        lease.dates.distributableDate = lease.dates.rentDueDate;

        emit LeaseCreated(leaseId, lease);
    }

    function submitDeposit(uint256 _leaseId) external nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(_msgSender() == lease.lessee, "[Lease Agreement] Only the Lessee can submit the deposit");
        require(!lease.securityDeposit.paid, "[Lease Agreement] Security deposit already paid");

        fundsManager.store(_leaseId, paymentToken, lease.securityDeposit.amount, _msgSender());
        lease.securityDeposit.paid = true;

        emit LeaseDepositSubmited(_leaseId, lease.securityDeposit.amount);
    }

    function setDueDate(uint256 _leaseId, uint256 _newDueDate) public {
        Lease storage lease = leases[_leaseId];
        require(_msgSender() == lease.lessor, "[Lease Agreement] Only the Lessor can set due date");
        require(
            _newDueDate >= (lease.dates.rentDueDate + 1 * MONTH),
            "[Lease Agreement] New rent due date must be at least a month after current one"
        );

        lease.dates.rentDueDate = _newDueDate;

        emit LeaseDueDateChanged(_leaseId, _newDueDate);
    }

    function payRent(uint256 _leaseId) external nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(_msgSender() == lease.lessee, "[Lease Agreement] Only the Lessee can pay rent");
        require(lease.securityDeposit.paid, "[Lease Agreement] Security deposit must be paid first");
        require(
            block.timestamp >= lease.dates.startDate && block.timestamp <= lease.dates.endDate,
            "[Lease Agreement] Outside of lease duration"
        );

        RentPaymentInfo memory rentInfo = calculateRentPaymentInfo(_leaseId);
        lease.unclaimedRentAmount += rentInfo.totalBalance;
        lease.dates.rentDueDate += (rentInfo.unpaidMonths) * (1 * MONTH);
        fundsManager.store(_leaseId, paymentToken, rentInfo.totalBalance, _msgSender());

        emit LeaseDueDateChanged(_leaseId, lease.dates.rentDueDate);
        emit LeasePaymentMade(_leaseId, rentInfo.totalBalance, lease.unclaimedRentAmount);
    }

    function distributeRent(uint256 _leaseId) public nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(
            _msgSender() == lease.lessor || _msgSender() == lease.agent,
            "[Lease Agreement] Caller must be the Lessor or the Agent"
        );
        require(lease.unclaimedRentAmount > 0, "[Lease Agreement] No rent to distribute");
        uint256 nbMonthSinceLastDistribute = 0;
        require(
            block.timestamp > lease.dates.distributableDate,
            "[Lease Agreement] Rent can only be distributed past the distributable date"
        );

        nbMonthSinceLastDistribute = (block.timestamp - lease.dates.distributableDate) / (1 * MONTH) + 1;
        uint256 totalToClaim = lease.unclaimedRentAmount;
        uint256 agentAmount = 0;

        if (lease.agent != address(0)) {
            agentAmount = (totalToClaim * lease.agentPercentage) / 100;
            fundsManager.widthdraw(_leaseId, paymentToken, uint32(agentAmount), lease.agent);
        }

        uint256 lessorAmount = totalToClaim - agentAmount;
        fundsManager.widthdraw(_leaseId, paymentToken, uint32(lessorAmount), lease.lessor);
        lease.unclaimedRentAmount = 0;
        lease.dates.distributableDate += nbMonthSinceLastDistribute * (1 * MONTH);

        emit LeaseRentDistributed(
            _leaseId,
            uint32(lessorAmount),
            uint32(agentAmount),
            lease.dates.distributableDate,
            block.timestamp
        );
    }

    function extendLease(uint256 _leaseId, uint256 _extensionPeriod) external {
        Lease storage lease = leases[_leaseId];
        require(_msgSender() == lease.lessee, "[Lease Agreement] Only the Lessee can extend the lease");
        require(
            block.timestamp >= lease.dates.endDate - 45 days, // TODO: Configurable
            "[Lease Agreement] Extension can only be requested in the last 45 days"
        );
        require(lease.extensionCount < 2, "[Lease Agreement] Maximum extensions reached");

        lease.dates.endDate += _extensionPeriod;
        lease.rentAmount += (lease.rentAmount * 3) / 100;
        lease.extensionCount++;

        emit LeaseExtended(_leaseId, lease.dates.endDate, lease.rentAmount, lease.extensionCount);
    }

    function terminateLease(uint256 _leaseId) public nonReentrant {
        Lease storage lease = leases[_leaseId];
        require(_msgSender() == lease.lessor, "[Lease Agreement] Only the Lessor can terminate the lease");
        require(block.timestamp >= lease.dates.startDate, "[Lease Agreement] Lease has not started yet");
        RentPaymentInfo memory rentInfo = calculateRentPaymentInfo(_leaseId);
        bool shouldSendDepositToLessor = (rentInfo.unpaidMonths >= 3); // TODO: Configurable

        // Send security deposit to the lessor or the lessee
        address recipient = shouldSendDepositToLessor ? lease.lessor : lease.lessee;
        fundsManager.widthdraw(_leaseId, paymentToken, lease.securityDeposit.amount, recipient);

        // Send remaining rent to the lessor
        uint256 remainingBalance = fundsManager.balanceOf(_leaseId, paymentToken);
        if (remainingBalance > 0) {
            fundsManager.widthdraw(_leaseId, paymentToken, remainingBalance, lease.lessor);
        }

        leaseNFT.burn(_leaseId);
        delete leases[_leaseId];

        emit LeaseTerminated(_leaseId);
    }

    function calculateRentPaymentInfo(uint256 _leaseId) public view returns (RentPaymentInfo memory rentInfo) {
        Lease storage lease = leases[_leaseId];
        rentInfo.rentAmount = lease.rentAmount;
        //sidenote: rent due date is incremented by 1 month each time the rent is paid.(function payRent)
        if (block.timestamp > lease.dates.rentDueDate + (lease.gracePeriod * 1 days)) {
            //Function to calculate rentAmountIncrease increase
            rentInfo.rentAmount += (lease.rentAmount * lease.latePaymentFee) / 100;
            rentInfo.unpaidMonths = uint32((block.timestamp - lease.dates.rentDueDate) / (1 * MONTH) + 1);
            rentInfo.totalBalance = (rentInfo.unpaidMonths) * rentInfo.rentAmount;
        } else {
            rentInfo.unpaidMonths = 1;
            rentInfo.totalBalance = rentInfo.rentAmount;
        }
        return rentInfo;
    }

    function _verifySubdivisionOwnership(address _owner, uint256 _subdivisionId) internal view returns (bool) {
        return subdivisionNFT.isOwnerOfSubdivision(_owner, _subdivisionId);
    }

    function _verifyDeedOwnership(address _owner, uint256 _deedId) internal view returns (bool) {
        try deedNFT.ownerOf(_deedId) returns (address owner) {
            return owner == _owner;
        } catch {
            return false;
        }
    }
}
","keccak256":"0x1ab428a42aa274d425740e79ab902b3effe1374f29b755aafe3df85ba64ecf67","license":"AGPL-3.0"},"contracts/SubdivisionNFT.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./DeedNFT.sol";

//was erc1155, multiple ownership? because cant call ownerof
contract SubdivisionNFT is ERC1155, AccessControl {
    struct SubdivisionInfo {
        bytes ipfsDetailsHash;
        address owner;
        uint256 parentDeed;
    }

    uint256 private _nextsubTokenID;
    //rename var
    mapping(uint256 => SubdivisionInfo) private subdivisionInfoMap;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    DeedNFT public deedNFT;

    event SubdivisionMinted(
        address subdivisionOwner,
        uint256 subdivisionId,
        uint256 deedId,
        bytes ipfsDetailsHash,
        address minter
    );
    event SubdivisionBurned(address account, uint256 subdivisionId, uint256 deedId, bytes ipfsDetailsHash);
    event SubdivisionIpfsDetailsSet(uint256 tokenId, bytes ipfsDetailsHash);
    event SubdivisionInfoSet(uint256 tokenId, SubdivisionInfo info);

    constructor(string memory _uri, address _deedNFT) ERC1155(_uri) {
        require(_deedNFT != address(0), "[SubdivisionNFT] Invalid DeedNFT address");

        _nextsubTokenID = 1;
        _setupRole(MINTER_ROLE, _msgSender());
        deedNFT = DeedNFT(_deedNFT);
    }

    //Took out onlyRole(MINTER_ROLE) because we only want owner to be able to mint and not the contract deployer
    function mintSubdivision(SubdivisionInfo memory _info) public {
        require(
            _msgSender() == deedNFT.ownerOf(_info.parentDeed),
            "[SubdivisionNFT] Must be the owner of the parent deed"
        );
        require(deedNFT.canSubdivide(_info.parentDeed), "[SubdivisionNFT] Parent deed must be land or estate");

        _mint(_info.owner, _nextsubTokenID, 1, _info.ipfsDetailsHash);
        SubdivisionInfo storage subInfo = subdivisionInfoMap[_nextsubTokenID];
        subInfo.ipfsDetailsHash = _info.ipfsDetailsHash;
        subInfo.owner = _info.owner;
        subInfo.parentDeed = _info.parentDeed;

        emit SubdivisionMinted(_info.owner, _nextsubTokenID, _info.parentDeed, _info.ipfsDetailsHash, _msgSender());
        _nextsubTokenID = _nextsubTokenID + 1;
    }

    function batchMint(SubdivisionInfo[] memory _infos) public {
        for (uint i = 0; i < _infos.length; i++) {
            mintSubdivision(_infos[i]);
        }
    }

    function setIpfsDetailsHash(uint256 _subTokenId, bytes memory _ipfsDetailsHash) public virtual {
        require(balanceOf(_msgSender(), _subTokenId) > 0, "[SubdivisionNFT] Must be owner of subNFT to set IPFS hash");

        SubdivisionInfo storage subInfo = subdivisionInfoMap[_subTokenId];
        subInfo.ipfsDetailsHash = _ipfsDetailsHash;

        emit SubdivisionIpfsDetailsSet(_subTokenId, _ipfsDetailsHash);
    }

    function burnSubdivision(uint256 _subTokenId) public {
        require(
            isOwnerOfSubdivision(_msgSender(), _subTokenId) == true,
            "[SubdivisionNFT] Sender must be owner of the subdivision to burn it"
        );

        _burn(_msgSender(), _subTokenId, 1);

        emit SubdivisionBurned(
            _msgSender(),
            _subTokenId,
            subdivisionInfoMap[_subTokenId].parentDeed,
            subdivisionInfoMap[_subTokenId].ipfsDetailsHash
        );
    }

    function getParentDeed(uint256 _subTokenId) public view returns (uint256) {
        SubdivisionInfo storage info = subdivisionInfoMap[_subTokenId];
        return info.parentDeed;
    }

    function isOwnerOfSubdivision(address _owner, uint256 _subTokenId) public view returns (bool) {
        return balanceOf(_owner, _subTokenId) > 0;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
