","keccak256":"0xa1e8e83cd0087785df04ac79fb395d9f3684caeaf973d9e2c71caef723a3a5d6","license":"MIT"},"contracts/DeedNFT.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

contract DeedNFT is ERC721, AccessControl {
    struct DeedInfo {
        bytes ipfsDetailsHash;
        AssetType assetType;
        uint256 price;
        string deedAddress;
        bool isValidated;
    }
    uint256 private _nextDeedId;
    mapping(uint256 => DeedInfo) private deedInfoMap;
    bytes32 public constant VALIDATOR_ROLE = keccak256("VALIDATOR_ROLE");

    enum AssetType {
        Land,
        Vehicle,
        Estate,
        CommercialEquipment
    }

    event DeedNFTMinted(uint256 deedId, DeedInfo deedInfo, address minter);
    event DeedNFTBurned(uint256 deedId);
    event DeedNFTAssetValidationSet(uint256 deedId, bool isValid);
    event DeedNFTIpfsDetailsSet(uint256 deedId, bytes newIpfsDetailsHash);
    event DeedNFTPriceUpdated(uint256 deedId, uint256 newPrice);
    event DeedNFTAssetTypeSet(uint256 deedId, AssetType newAssetType);

    constructor() ERC721("DeedNFT", "DEED") {
        _nextDeedId = 1;
        _setupRole(VALIDATOR_ROLE, _msgSender());
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
    }

    modifier deedExists(uint256 _deedId) {
        require(_exists(_deedId), string.concat("[DeedNFT] Deed does not exist with id ", Strings.toString(_deedId)));
        _;
    }

    modifier onlyOwner(uint256 _deedId) {
        require(
            _msgSender() == ownerOf(_deedId),
            string.concat("[DeedNFT] Must be owner of the Deed with id ", Strings.toString(_deedId))
        );
        _;
    }

    function mintAsset(
        address _owner,
        bytes memory _ipfsDetailsHash,
        AssetType _assetType,
        string memory _deedAddress
    ) public {
        _mint(_owner, _nextDeedId);
        DeedInfo storage deedInfo = deedInfoMap[_nextDeedId];
        deedInfo.ipfsDetailsHash = _ipfsDetailsHash;
        deedInfo.assetType = _assetType;
        deedInfo.deedAddress = _deedAddress;
        deedInfo.isValidated = false;
        emit DeedNFTMinted(_nextDeedId, deedInfo, _msgSender());
        _nextDeedId = _nextDeedId + 1;
    }

    function burn(uint256 _deedId) public onlyOwner(_deedId) {
        _burn(_deedId);
        emit DeedNFTBurned(_deedId);
    }

    function setAssetValidation(uint256 _deedId, bool _isValid) public onlyRole(VALIDATOR_ROLE) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.isValidated = _isValid;
        emit DeedNFTAssetValidationSet(_deedId, _isValid);
    }

    function setPrice(uint256 _deedId, uint32 _newPrice) public deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.price = _newPrice;
        emit DeedNFTPriceUpdated(_deedId, _newPrice);
    }

    function setIpfsDetailsHash(
        uint256 _deedId,
        bytes memory _ipfsDetailsHash
    ) public virtual deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.ipfsDetailsHash = _ipfsDetailsHash;
        emit DeedNFTIpfsDetailsSet(_deedId, _ipfsDetailsHash);
    }

    function setAssetType(
        uint256 _deedId,
        AssetType _assetType
    ) internal virtual deedExists(_deedId) onlyOwner(_deedId) {
        DeedInfo storage deedInfo = deedInfoMap[_deedId];
        deedInfo.assetType = _assetType;
        emit DeedNFTAssetTypeSet(_deedId, _assetType);
    }

    function getDeedInfo(uint256 _deedId) public view deedExists(_deedId) returns (DeedInfo memory) {
        return deedInfoMap[_deedId];
    }

    function addValidator(address _validator) public onlyRole(DEFAULT_ADMIN_ROLE) {
        grantRole(VALIDATOR_ROLE, _validator);
    }

    function removeValidator(address _validator) public onlyRole(DEFAULT_ADMIN_ROLE) {
        revokeRole(VALIDATOR_ROLE, _validator);
    }

    function canSubdivide(uint256 _deedId) external view returns (bool) {
        AssetType assetType = getDeedInfo(_deedId).assetType;
        return assetType == AssetType.Land || assetType == AssetType.Estate;
    }

    function supportsInterface(bytes4 _interfaceId) public view virtual override(ERC721, AccessControl) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }
}
","keccak256":"0x215c2b2424c1ddf463eb1266fa223b479f1f48802335c53d38a7f93acb096f01","license":"AGPL-3.0"},"contracts/SubdivisionNFT.sol":{"content":"// SPDX-License-Identifier: AGPL-3.0
pragma solidity ^0.8.17;
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./DeedNFT.sol";

//was erc1155, multiple ownership? because cant call ownerof
contract SubdivisionNFT is ERC1155, AccessControl {
    struct SubdivisionInfo {
        bytes ipfsDetailsHash;
        address owner;
        uint256 parentDeed;
    }

    uint256 private _nextsubTokenID;
    //rename var
    mapping(uint256 => SubdivisionInfo) private subdivisionInfoMap;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    DeedNFT public deedNFT;

    event SubdivisionMinted(
        address subdivisionOwner,
        uint256 subdivisionId,
        uint256 deedId,
        bytes ipfsDetailsHash,
        address minter
    );
    event SubdivisionBurned(address account, uint256 subdivisionId, uint256 deedId, bytes ipfsDetailsHash);
    event SubdivisionInfoSet(uint256 tokenId, SubdivisionInfo info);

    constructor(string memory _uri, address _deedNFT) ERC1155(_uri) {
        require(_deedNFT != address(0), "[SubdivisionNFT] Invalid DeedNFT address");

        _nextsubTokenID = 1;
        _setupRole(MINTER_ROLE, _msgSender());
        deedNFT = DeedNFT(_deedNFT);
    }

    // Took out onlyRole(MINTER_ROLE) because we only want owner to be able to mint and not the contract deployer
    function mintSubdivision(SubdivisionInfo memory _info) public {
        require(
            _msgSender() == deedNFT.ownerOf(_info.parentDeed),
            "[SubdivisionNFT] Sender must be the owner of the parent deed"
        );
        require(deedNFT.canSubdivide(_info.parentDeed), "[SubdivisionNFT] Parent deed must be land or estate");

        _mint(_info.owner, _nextsubTokenID, 1, _info.ipfsDetailsHash);
        SubdivisionInfo storage subInfo = subdivisionInfoMap[_nextsubTokenID];
        subInfo.ipfsDetailsHash = _info.ipfsDetailsHash;
        subInfo.owner = _info.owner;
        subInfo.parentDeed = _info.parentDeed;

        emit SubdivisionMinted(_info.owner, _nextsubTokenID, _info.parentDeed, _info.ipfsDetailsHash, _msgSender());
        _nextsubTokenID = _nextsubTokenID + 1;
    }

    function batchMint(SubdivisionInfo[] memory _infos) public {
        for (uint i = 0; i < _infos.length; i++) {
            mintSubdivision(_infos[i]);
        }
    }

    function setInfo(uint256 _subTokenId, SubdivisionInfo memory _newInfo) public virtual {
        require(
            isOwnerOfSubdivision(_msgSender(), _subTokenId) == true,
            "[SubdivisionNFT] Sender Must be owner of subdivision to set info"
        );

        subdivisionInfoMap[_subTokenId] = _newInfo;

        emit SubdivisionInfoSet(_subTokenId, _newInfo);
    }

    function burnSubdivision(uint256 _subTokenId) public {
        require(
            isOwnerOfSubdivision(_msgSender(), _subTokenId) == true,
            "[SubdivisionNFT] Sender must be owner of the subdivision to burn it"
        );

        _burn(_msgSender(), _subTokenId, 1);

        emit SubdivisionBurned(
            _msgSender(),
            _subTokenId,
            subdivisionInfoMap[_subTokenId].parentDeed,
            subdivisionInfoMap[_subTokenId].ipfsDetailsHash
        );
    }

    function getParentDeed(uint256 _subTokenId) public view returns (uint256) {
        SubdivisionInfo storage info = subdivisionInfoMap[_subTokenId];
        return info.parentDeed;
    }

    function isOwnerOfSubdivision(address _owner, uint256 _subTokenId) public view returns (bool) {
        return balanceOf(_owner, _subTokenId) > 0;
    }

    function supportsInterface(
        bytes4 _interfaceId
    ) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(_interfaceId);
    }
}
